<?php class cookie{public static function get($key){if(isset($_COOKIE[$key]))return $_COOKIE[$key];else return null;}public static function set($key,$val,$life=0){$result=false;if($life<0)ob_clean();$result=setcookie($key,$val,$life?(time()+$life):0,'/');return $result;}public static function del($key){unset($_COOKIE[$key]);return setcookie($key,'',-604800);}public static function clear(){foreach($_COOKIE as $key=>$val){setcookie($key,'',-604800);}}}?>