<?php require 'db.php';require 'aes.php';class data{public static function link($key){$configs=$GLOBALS['ini_db'];if(!key_exists($key,$configs))trigger_error('101|key '.$key.' undefined in $ini_db in file conf/web.php');$config=$configs[$key];$driver=$config['type'];require_once $driver.'.php';$db=new $driver();if($db instanceof db){$db->connect($config);return $db;}else{trigger_error('102|file '.$driver.'.php not implements interface db');}}public static function wildcard($str){if($str&&(!is_numeric($str))){$str=strtr($str,array('_'=>'','%'=>''));}return $str;}}?>