<?php class date{public static function add($interval,$number,$unixtime){$utctime=getdate($unixtime);$y=$utctime['year'];$m=$utctime['mon'];$d=$utctime['mday'];$h=$utctime['hours'];$i=$utctime['minutes'];$s=$utctime['seconds'];switch($interval){case 'y':$y+=$interval;break;case 'm':$m+=$interval;break;case 'd':$d+=$interval;break;case 'h':$h+=$interval;break;case 'i':$i+=$interval;break;case 's':$s+=$interval;break;}return mktime($h,$i,$s,$m,$d,$y);}public static function diff($interval,$unixtime1,$unixtime2){switch(strtolower($interval)){case 'y':$utctime1=getdate($unixtime1);$utctime2=getdate($unixtime2);$result=$utctime2['year']-$utctime1['year'];break;case 'm':$utctime1=getdate($unixtime1);$utctime2=getdate($unixtime2);$result=($utctime2['year']-$utctime1['year'])*12+($utctime2['mon']-$utctime1['mon']);break;case 'd':$result=intval(($unixtime2-$unixtime1)/86400);break;case 'h':$result=intval(($unixtime2-$unixtime1)/3600);break;case 'i':$result=intval(($unixtime2-$unixtime1)/60);break;case 's':$result=$unixtime2-$unixtime1;break;}return $result;}}?>