<?php class file{public static function write($path,$content,$append=true){$result=false;if($append){$flag='ab';$content.="\n";}else{$flag='wb';}$handle=fopen($path,$flag);if($handle){if(flock($handle,LOCK_EX)){$result=fwrite($handle,$content);flock($handle,LOCK_UN);}fclose($handle);}return $result;}public static function mkdir($path){$p='';$result=false;$dir=explode('/',$path);for($i=0;$i<count($dir);$i++){$p.=$dir[$i].'/';if(!file_exists($p)){$result=mkdir($p);if(!$result)break;}}return $result;}}?>