  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `app_id` int(11) unsigned NOT NULL COMMENT '应用ID',
  `apk_file_id` int(11) unsigned NOT NULL COMMENT '上传的apkID',
